-- 1. Insert the output, generated by a stored PROCEDURE into TABLE

USE DEMODATABASE 
GO 
CREATE PROCEDURE SPGET_STUDENT_ADMISSIONDATE 
AS 
  BEGIN 
      SELECT ISNULL(FIRSTNAME, '') + ' ' + ISNULL(LASTNAME, '') AS STUDENTNAME, 
             ADMISSIONDATE, 
             GRADE 
      FROM   STUDENTS 
      WHERE  ADMISSIONDATE IS NOT NULL 
  END
  
INSERT INTO #TEMPSTUDENTS 
EXECUTE SPGET_STUDENT_ADMISSIONDATE 
